#include <fstream>

#include <opencv2/opencv.hpp>

std::vector<std::string> load_class_list()
{
    std::vector<std::string> class_list;
    std::ifstream ifs("/home/suman-singh/Downloads/yolov5-master/temp.txt");
    std::string line;
    while (getline(ifs, line))
    {
